import React, { Component } from 'react';
import './App.css';
import Dashboard from './components/Dashboard.jsx'
import UnsafeScriptsWarning from "./components/UnsafeScriptsWarning";

class App extends Component {

  state = {
    hasError: false,
    showSpinner: true
  }

  static getDerivedStateFromError(error) {
    // Update state so the next render will show the fallback UI.
    console.log('Ocurrió algún error');
    return { hasError: true };
  }

  componentDidCatch(error, info) {
    // You can also log the error to an error reporting service
    console.log(error, info);
  }

  hideSpinner = () => {
    this.setState({showSpinner: false});
  }

  handleClick() {
  this.setState(state => ({
    isToggleOn: !state.isToggleOn
    }));
  }

  render() {
    if (this.state.hasError) {
      return <UnsafeScriptsWarning />;
    }
    if(this.state.isToggleOn){
    return (
      <div className="App">
      <button onClick={this.handleClick}>
          {this.state.isToggleOn ? 'DESCONECTAR SOCKET' : 'CONECTAR SOCKET'}
        </button>
        <Dashboard hideSpinner={this.hideSpinner} showSpinner={this.state.showSpinner} />
      </div>
    );
    }else{
        return (
            <div style={{ textAlign: "center" }}>
            <p>Debe conectarse al socket</p>

            <button onClick={this.handleClick}>
              {this.state.isToggleOn ? 'DESCONECTAR SOCKET' : 'CONECTAR SOCKET'}
            </button>
          </div>);
      }
  }
}

export default App;
